package chaptor2;

public class Casting1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d= 100.0;
		
		System.out.println(d);
		
		int i = 100;
		
		int result = i + (int)d;
		
		System.out.println(d);
		System.out.println(i);
		System.out.println(result);
	}

}
