package chaptor2;

public class CastingOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = 'a';
		System.out.println(c);
		
		int n1 = 1;
		System.out.println(c+n1);
		
		int n2 = 10;
		System.out.println(n2);
		
		float n3 = 10.0f;
		System.out.println(n2+n3);
	}

}
